web3 = Web3(Web3.HTTPProvider(INFURA_URL))
print("web3: ", web3.is_connected())

my_account = web3.eth.account.from_key(PRIVATE_KEY)
contract = web3.eth.contract(contract_address, abi=token_abi)
m_balance = contract.functions.balanceOf(my_account.address).call()
r_balance = contract.functions.balanceOf(receiver_address).call()
amount = 100
print(f"My balance: {m_balance}")
print(f"Receiver balance: {r_balance}")
assert m_balance >= amount, "Error, Not enough balance"

raw_txn = {
    "from": my_account.address,
    "gasPrice": web3.eth.gas_price,
    "gas": 200000,
    "to": contract_address,
    "value": "0x0",
    "data": contract.encodeABI('transfer', args=(receiver_address, amount)),
    "nonce": web3.eth.get_transaction_count(my_account.address)
}

signed_txn = web3.eth.account.sign_transaction(raw_txn, PRIVATE_KEY)
tx_hash = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
print("Transaction Completed:", web3.to_hex(tx_hash))
